mapper.select:
select user.id, user.username, role.role_name, user.enabled, user.gender, user.account, user.avatar from (select * from t_user
t_user.username like CONCAT('%', #{username}, '%')
) user left join t_user_role_relation user_role on user.id = user_role.user_id left join t_role role on role.id = user_role.role_id limit #{offset}, #{size}
